		private int RawKeyIndex;
		private byte[] RawKey;
		private int PlainCipherIndex;

		private void WritePlainCipher(byte[] plain)
		{
			using (AESCipher transformer = new AESCipher(RawKey))
			{
				byte[] cipher = new byte[16];
				transformer.EncryptBlock(plain, cipher);

				Writer.WriteLine(string.Format("P No.{0:D3} : {1} ", (PlainCipherIndex + 1), ToTestVectorHex(plain)));
				Writer.WriteLine(string.Format("C No.{0:D3} : {1} ", (PlainCipherIndex + 1), ToTestVectorHex(cipher)));
				Writer.WriteLine("");

				PlainCipherIndex++;
			}
		}

		private string ToTestVectorHex(byte[] data)
		{
			StringBuilder buff = new StringBuilder();

			for (int index = 0; index < data.Length; index++)
			{
				if (1 <= index)
				{
					buff.Append(" ");
				}
				buff.Append(((int)data[index]).ToString("X02"));
			}
			return buff.ToString();
		}
	}
